                print(f"- {livre['titre']} par {livre['auteur']} ({statut})")
        else:
            print("Aucun livre trouvé.")

    def emprunter_livre(self):
        """Emprunte un livre"""
        self.afficher_livres()

        try:
            livre_id = int(input("ID du livre à emprunter : "))
