def base10_vers_binaire(n):
    if n == 0:
        return '0'
    binaire = ''
    while n > 0:
        binaire = str(n % 2) + binaire
        n = n // 2
    return binaire

def binaire_vers_base10(binaire):
    decimal = 0
    puissance = 0
    for i in range(len(binaire) - 1, -1, -1):
        if binaire[i] == '1':
            decimal += 2 ** puissance
        puissance += 1
    return decimal
